from src.leetcode.shared.test_case_executor import TestCaseExecutor
from itertools import combinations

class Solution(object):
    def ThreeSum(self, nums: list[int]) -> list[int]:
        if len(nums) == 3 and nums[0] + nums[1] + nums[2] == 0:
            return [nums]
        elif len(nums) < 3:
            return []
            
        m: dict[int, set[int]] = {}
        for idx, value in enumerate(nums):
            if value not in m:
                m[value] = set([idx])
            m[value].add(idx)
        
        r: set[tuple[int, int, int]] = set()

        for i, j in combinations(enumerate(nums), 2):
            t = 0 - (i[1] + j[1])
            c = m.get(t)
            if c and len(c) > len({i[0], j[0]} & c):
                r.add(tuple(sorted([i[1], j[1], t])))
                continue
        return [list(i) for i in r]

